import '../App.css';
import React, { useEffect, useState } from "react";
import { Routes, Route, Link } from "react-router-dom";

const desc = [
    {
        "idx": "0",
        "path": "/Alarm",
        "head": "Alarm",
        "desc": "시간과 내용을 설정하고, 설정한 시간이 되었을 때 알려줍니다.",
        "imgSrc": "",
        "videoSrc": "1번"
    },
    {
        "idx": "1",
        "path": "/timer",
        "head": "Timer",
        "desc": "시간의 흐름을 기록, 저장할 수 있습니다.",
        "imgSrc": "",
        "videoSrc": "2번"
    },
    {
        "idx": "2",
        "path": "/checklist",
        "head": "CheckList",
        "desc": "하루의 일정을 설정하고 수행 여부를 관리할 수 있습니다.",
        "imgSrc": "",
        "videoSrc": "3번"
    },
    {
        "idx": "3",
        "path": "/weather",
        "head": "Weather",
        "desc": "원하는 도시를 검색하고 해당 도시의 날씨정보를 확인할 수 있습니다.",
        "imgSrc": "",
        "videoSrc": "4번"
    }
]
const options = [
    {

    }
]

function Modal(props) {
    return (
        <div id="modal">
            <div className="modalBg" onClick={() => { props.modalController(); }}></div>
            <section>
                <video src={props.movie} controls></video>
            </section>
        </div >
    )
}

function Main() {
    const [modalActive, setModalActive] = useState(false);
    const [modalComp, setModalComp] = useState(null);
    const [videoSrc, setVideoSrc] = useState(null);

    //모달 생성
    useEffect(() => {
        if (modalActive === true && modalComp === null) {
            setModalComp(<Modal movie={videoSrc} modalController={modalUnActive} />)
        }
    }, [modalActive])

    //모달 ESC키 이벤트
    useEffect(() => {
        document.addEventListener("keydown", () => {
            if (modalActive === true && modalComp !== null) {
                modalUnActive();
            }
        })
    })

    //모달창 제거
    function modalUnActive() {
        setModalActive(false);
        setModalComp(null);
    }

    //모달 세팅
    function previewModalHandler(e) {
        const idx = e.currentTarget.className;
        const video = desc[idx].videoSrc;
        setVideoSrc(video);
        setModalActive(true);
        // console.log(e.currentTarget, idx, body)
    }


    return (
        <>
            {modalComp}
            <h1 id="indexHead">WELCOME !</h1>
            <article className="indexContainer first">
                <div className='flex'>
                    {desc.map((desc) => (
                        <section key={desc.idx} className='indexBlock'>
                            <Link to={desc.path} className="navBlocks">
                                <img src={desc.imgSrc} alt={desc.imgSrc}></img>
                                <h2>{desc.head}</h2>
                                <p>{desc.desc}</p>
                            </Link>
                            <span className={desc.idx} onClick={(event) => {
                                previewModalHandler(event);
                            }}>미리보기</span>
                        </section>
                    ))}
                </div>
            </article>
            <article className='indexContainer second'>
                <div className="flex">
                    {desc.map((desc) => (
                        <section key={desc.idx} className="infoBlocks">
                            <h2>기능 설명</h2>
                            <p>{desc.idx}번 기능</p>
                        </section>
                    ))}

                </div>
            </article>
        </>
    )
}

export default Main
