import './App.css';
import React, { useState } from "react";
import { Routes, Route, Link } from "react-router-dom";

import Alarm from "./pages/Alarm";
import Timer from "./pages/timer";
import Weather from "./pages/weather";
import CheckList from './pages/CheckList';

const desc = [
  {
    "idx": "1",
    "path": "/Alarm",
    "head": "Alarm",
    "desc": "시간과 내용을 설정한 뒤, 해당 시간이 되면 알려주는 기능.",
    "imgSrc": "",
    "videoSrc": "1번"
  },
  {
    "idx": "2",
    "path": "/timer",
    "head": "Timer",
    "desc": "시간의 흐름을 기록, 메모와 함께 저장할 수 있습니다.",
    "imgSrc": "",
    "videoSrc": "2번"
  },
  {
    "idx": "3",
    "path": "checklist",
    "head": "CheckList",
    "desc": "매일 반복적인 루틴을 설정, 확인할 수 있습니다.",
    "imgSrc": "",
    "videoSrc": "3번"
  },
  {
    "idx": "4",
    "path": "weather",
    "head": "Weather",
    "desc": "지도 위에 표시된 주요 도시를 클릭하면 간략한 날씨정보를 확인할 수 있습니다.",
    "imgSrc": "",
    "videoSrc": "4번"
  }
]

function Modal(props) {
  return (
    <div id="modal">
      <div></div>
      <section>
        <video src={props.movie} controls></video>
      </section>
    </div>
  )
}

function Main() {
  const [modalActive, setModalActive] = useState(false);
  const [modalComp, setModalComp] = useState(null);
  function previewModalHandler(e) {
    const body = document.querySelector('body');
    const idx = e.target.className;
    const videoSrc = desc[idx].videoSrc;
    // console.log(e.currentTarget, idx, body)
    setModalComp(<Modal movie={videoSrc}></Modal>)
    setModalActive(true);
    if ({ modalActive } === true) {
      return (
        { modalComp }
      )
    } else { }

  }

  return (
    <>
      <h1 id="indexHead">WELCOME !</h1>
      <article id="indexContainer">
        <div className='flex'>
          {/* <div className='indexBlock'>
