import React, { useState, useEffect } from 'react';
import useInterval from '../hooks/useInterval';

function TimerTimer() {
    const [timerHour, setTimerHour] = useState(0);
    const [timerMin, setTimerMin] = useState(0);
    const [timerSec, setTimerSec] = useState(0);
    const [timerMilSec, setTimerMilSec] = useState(0);

    const [hourState, setHourState] = useState(false);

    //인터벌 훅에 null을 주면 정지, 시간값을 주면 실행
    const [intervalDelay, setIntervalDelay] = useState(10);

    const [intervalMode, setIntervalMode] = useState("ON");

    let timeView = null;

    //60분이 넘어가면 분 초기화, 시 표시
    if (timerMin === 60) {
        setHourState(true);
        setTimerMin(0);
    }


    useInterval(() => {
        if (intervalMode === "ON") {
            timer();
        } else if (intervalMode === "OFF") {

        }
    }, intervalDelay);



    function timer() {
        setTimerMilSec(timerMilSec + 1);
        if (timerMilSec === 60) {
            setTimerMilSec(0);
            setTimerSec(timerSec + 1);
        }
        if (timerSec === 60) {
            setTimerSec(0);
            setTimerMin(timerMin + 1);
        }
        if (timerMin === 60) {
            setTimerMin(0);
            setTimerHour(timerHour + 1);
        }
    }



    useEffect(() => {
        console.log("timer render");
    })
    return (
        <section>
            <h2>
                <span>{timerHour < 10 ? `0${timerHour}` : timerHour}</span>
                <span>{timerMin < 10 ? `0${timerMin}` : timerMin}</span>
                :
                <span>{timerSec < 10 ? `0${timerSec}` : timerSec}</span>
                .
                <span>{timerMilSec < 10 ? `0${timerMilSec}` : timerMilSec}</span>
            </h2>
        </section>
    )
}

export default TimerTimer;
