import React, { useState } from 'react';
import db from '../data/data.json';
import useInterval from '../hooks/useInterval';

import Nav from './Nav';
import AlarmHead from './AlarmHead';
import AlarmRead from './AlarmRead';
import AlarmCreate from './AlarmCreate';
import AlarmUpdate from './AlarmUpdate';
function nowTime() {
	const date = new Date();
	let hour = date.getHours();
	let minute = date.getMinutes();
	let second = date.getSeconds();
	let day = date.getDate();
	let month = date.getMonth();
	let year = date.getFullYear();

	const [now_hour, setHour] = useState(hour);
	const [now_min, setMinute] = useState(minute);
	const [now_second, setSecond] = useState(second);
	const [now_day, setDay] = useState(day);
	const [now_month, setMonth] = useState(month + 1);
	const [now_year, setYear] = useState(year);

	useInterval(() => {
		const date = new Date();
		let hour = date.getHours();
		let minute = date.getMinutes();
		let second = date.getSeconds();
		let day = date.getDate();
		let month = date.getMonth();
		let year = date.getFullYear();

		setHour(hour);
		setMinute(minute);
		setSecond(second);
		setDay(day);
		setMonth(month + 1);
		setYear(year);

	}, 1000);
}

function Alarm() {
	const [mode, setMode] = useState('READ');
	// const [id, setId] = useState(null);
	const [nextId, setNextId] = useState(db.alarm === null ? 1 : db.alarm.length);
	// const [alarm, setAlarm] = useState([
	// 	{ id: 1, memo: '기상', hour: '07시', min: '30분' },
	// 	{ id: 2, memo: '아침먹기', hour: '08시', min: '15분' }
	// ]);
	const [getRead, setRead] = useState([]);
	function goData(data) {
		setRead(data);
	}
	let content = null;

	if (mode === 'READ') {
		content = <AlarmRead goCreate={() => { setMode('CREATE') }}
			goUpdate={() => { setMode("UPDATE"); }}
			goData={(fromRead) => { goData(fromRead); }}></AlarmRead >
	}
	else if (mode === 'CREATE') {
		content = <AlarmCreate
			goRead={() => { setMode("READ"); }}
			onCreate={() => {
				setNextId(nextId + 1);
				setMode('READ');
			}}></AlarmCreate>
	}
	else if (mode === 'UPDATE') {
		content = <AlarmUpdate upData={getRead} goRead={() => { setMode("READ") }} onUpdate={() => { setMode('READ') }} ></AlarmUpdate>
	}
	return (
		<>
			<Nav></Nav>
			<article className="contents">
				<AlarmHead></AlarmHead>
				{content}
			</article>
		</>
	)
}

export default Alarm
