import React, { useEffect, useState } from 'react';
import db from '../data/data.json';
import useInterval from '../hooks/useInterval';

import Nav from './Nav';
import AlarmHead from './AlarmHead';
import AlarmRead from './AlarmRead';
import AlarmCreate from './AlarmCreate';
import AlarmUpdate from './AlarmUpdate';

function Alarm() {
	//현재시간 세팅
	const date = new Date();
	const [nowTimeHour, setNowTimeHour] = useState(date.getHours);
	const [nowTimeMin, setNowTimeMin] = useState(date.getMinutes);

	//알람 울릴 시간 세팅
	const [ringTimeHour, setRingTimeHour] = useState(null);
	const [ringTimeMin, setRingTimeMin] = useState(null);
	const [ringMemo, setRingMemo] = useState(null);

	//AlarmHead컴포넌트로부터 현재시간 받아오기
	function getNowTime(hour, min) {
		setNowTimeHour(hour);
		setNowTimeMin(min);
		console.log("now time", nowTimeHour, nowTimeMin)
	}

	//Read컴포넌트로부터 첫번째 리스트 시간정보 받아오기
	function getRingTime(hour, min, memo) {
		setRingTimeHour(hour);
		setRingTimeMin(min);
		setRingMemo(memo)
		console.log("ring time", ringTimeHour, ringTimeMin, ringMemo)
	}

	useEffect(() => {
		if (nowTimeHour === ringTimeHour && nowTimeMin === ringTimeMin) {
			window.alert("알람 울렸어요. : ", ringMemo);
		}
	})

	//CRUD모드 세팅
	const [mode, setMode] = useState('READ');

	const [nextId, setNextId] = useState(db.alarm === null ? 1 : db.alarm.length);
	const [getRead, setRead] = useState([]);
	function goData(data) {
		setRead(data);
	}
	let content = null;

	if (mode === 'READ') {
		content = <AlarmRead goCreate={() => { setMode('CREATE') }}
			goUpdate={() => { setMode("UPDATE"); }}
			goData={(fromRead) => { goData(fromRead); }}
			getRingTime={(hour, min, memo) => { getRingTime(hour, min, memo) }} />
	}
	else if (mode === 'CREATE') {
		content = <AlarmCreate goRead={() => { setMode("READ"); }} />
	}
	else if (mode === 'UPDATE') {
		content = <AlarmUpdate upData={getRead} goRead={() => { setMode("READ") }} onUpdate={() => { setMode('READ') }} />
	}
	return (
		<>
			<Nav></Nav>
			<article className="contents">
				<AlarmHead getTime={(hour, min) => { getNowTime(hour, min) }} />
				{content}
			</article>
		</>
	)
}

export default Alarm
