import '../App.css';
import React from "react";
import { Link, useLocation } from "react-router-dom";



function Nav() {
    const nowloc = useLocation();
    function navBold() {
        const path = nowloc.pathname.substring(1);
        const navElement = document.querySelectorAll("#nav>a");
        for (let i = 0; i < navElement.length; i++) {
            if (navElement[i].innerHTML == path) {
                navElement[i].classList = 'nowPages';
            }
        }
    }


    return (
        <header>
            <nav id="nav">
                <Link to="/" className="str">home</Link>
                <Link to="/alarm" className="str">alarm</Link>
                <Link to="/timer" className="str">timer</Link>
                <Link to="/checklist" className="str">checkList</Link>
                <Link to="/weather" className="str">weather</Link>
            </nav>
        </header>
    )
}

export default Nav
