import React, { useEffect, useState } from 'react';
import useInterval from '../hooks/useInterval.js';

import Nav from './Nav';
import AlarmHead from './AlarmHead';
import AlarmRead from './AlarmRead';
import AlarmCreate from './AlarmCreate';
import AlarmUpdate from './AlarmUpdate';

function Alarm() {
	//현재시간 세팅
	const [now_hour, setHour] = useState(hour);
	const [now_min, setMinute] = useState(minute);
	const [now_second, setSecond] = useState(second);
	const [now_day, setDay] = useState(day);
	const [now_month, setMonth] = useState(month + 1);
	const [now_year, setYear] = useState(year);

	useInterval(() => {

		const date = new Date();
		let hour = date.getHours();
		let minute = date.getMinutes();
		let second = date.getSeconds();
		let day = date.getDate();
		let month = date.getMonth();
		let year = date.getFullYear();
	}, 1000)
	const [nowTimeHour, setNowTimeHour] = useState(undefined);
	const [nowTimeMin, setNowTimeMin] = useState(undefined);
	const [match, setMatch] = useState(true);
	const [swc, setSwc] = useState(true);

	//알람 울릴 시간 세팅
	const [ringTimeHour, setRingTimeHour] = useState(null);
	const [ringTimeMin, setRingTimeMin] = useState(null);
	const [ringMemo, setRingMemo] = useState(null);

	//AlarmHead컴포넌트로부터 현재시간 받아오기
	function getNowTime(hour, min) {
		setNowTimeHour(Number(hour));
		setNowTimeMin(Number(min));
	}

	//Read컴포넌트로부터 첫번째 리스트 시간정보 받아오기
	function getRingTime(hour, min, memo) {
		setRingTimeHour(Number(hour));
		setRingTimeMin(Number(min));
		setRingMemo(memo);
	}

	//알람 매칭 이벤트
	function timeMatch() {
		if (swc && match && nowTimeHour === ringTimeHour && nowTimeMin === ringTimeMin) {
			setSwc(false);
			setTimeout(() => { setSwc(true) }, 60000)
			window.alert("알람알람 울렸어요 : " + ringMemo);
		} else if (nowTimeHour !== ringTimeHour || nowTimeMin !== ringTimeMin) {
			setMatch(false);
		} else if (!swc && nowTimeHour === ringTimeHour && nowTimeMin === ringTimeMin) {
			setMatch(false);
		} else if (swc) {
			setMatch(true);
		}
	}

	// useInterval(() => {
	// 	timeMatch();
	// }, 1000)



	//CRUD모드 세팅
	const [mode, setMode] = useState('READ');
	const [getRead, setRead] = useState([]);
	function goData(data) {
		setRead(data);
	}
	let content = null;

	if (mode === 'READ') {
		content = <AlarmRead hour={nowTimeHour} min={nowTimeMin} goCreate={() => { setMode('CREATE') }}
			goUpdate={() => { setMode("UPDATE"); }}
			goData={(fromRead) => { goData(fromRead); }}
			getRingTime={(hour, min, memo) => { getRingTime(hour, min, memo) }}
		/>
	}
	else if (mode === 'CREATE') {
		content = <AlarmCreate goRead={() => { setMode("READ"); }} />
	}
	else if (mode === 'UPDATE') {
		content = <AlarmUpdate upData={getRead} goRead={() => { setMode("READ") }} />
	}
	return (
		<>
			<Nav></Nav>
			<article className="contents">
				<AlarmHead match={() => { timeMatch() }} getTime={(hour, min) => { getNowTime(hour, min) }} />
				{content}
			</article>
		</>
	)
}

export default Alarm
