import React, { useState, useEffect } from 'react';
import useInterval from '../hooks/useInterval';

function TimerTimer() {
    const [timerHour, setTimerHour] = useState(1);
    const [timerMin, setTimerMin] = useState(100);
    const [timerSec, setTimerSec] = useState(0);
    const [timerMilSec, setTimerMilSec] = useState(0);

    const [hourState, setHourState] = useState(false);

    let timeView = null;

    console.log(timerHour, hourState, timerMin);

    if (timerMin >= 100) {
        setHourState(true);
        setTimerMin(0);
    }


    useEffect(() => {
        console.log("timer render");
    })
    return (
        <section>
            <h2>
                <span>{timerHour < 10 ? `0${timerHour}` : timerHour}</span>
                <span>{timerMin < 10 ? `0${timerMin}` : timerMin}</span>
                :
                <span>{timerSec < 10 ? `0${timerSec}` : timerSec}</span>
                .
                <span>{timerMilSec < 10 ? `0${timerMilSec}` : timerMilSec}</span>
            </h2>
        </section>
    )
}

export default TimerTimer;
