import React from 'react';


function AlarmCreate(props) {
    const selectHour = [];
    for (let i = 0; i < 24; i++) {
        selectHour.push(
            <option key={i} value={i}>{i}</option>
        )
    }
    const selectMin = [];
    for (let i = 0; i < 60; i++) {
        selectMin.push(
            <option key={i} value={i}>{i}</option>
        )
    }

    return (
        <div id="mainCreation">
            <form onSubmit={(event) => {
                const date = new Date();
                const thisHour = date.getHours();
                const thisMin = date.getMinutes();
                const thisSec = date.getSeconds();
                // console.log("now : ", thisHour, thisMin);
                event.preventDefault();
                const memo = event.target.memo.value;
                const selTime = event.target.time.value;
                const selMinute = event.target.minute.value;
                // console.log("set : ", selTime, selMinute);
                let over = false;
                if (Number(thisHour) < Number(selTime)) {
                    over = false;
                } else if (Number(thisHour) === Number(selTime) && Number(thisMin) < Number(selMinute)) {
                    over = false;

                } else {
                    over = true;

                }
                fetch('http://localhost:3001/alarm', {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({
                        id: null,
                        memo: memo,
                        hour: selTime,
                        min: selMinute,
                        over: over
                    }),
                }).then((res) => res.json())
                props.goRead();
            }}>
                <div id="selectBox">
                    <select name="time" required>
                        {selectHour}
                    </select>
                    <span>시</span>
                    <select name="minute" required>
                        {selectMin}
                    </select>
                    <span>분</span>
                </div>
                <div id="memoBox">
                    <input type="text" name="memo" placeholder='내용을 입력하세요.'></input>
                </div>
                <button type="button" onClick={(event) => {
                    event.preventDefault();
                    props.goRead();
                }}>BACK</button>
                <button type="submit">ADD</button>

            </form>

        </div>

    )
}

export default AlarmCreate
